SELECT t1.* FROM sys_project AS t1 INNER JOIN sys_user_department AS t2 ON t2.department_id = t1.department_id AND t1.del_flag = '0' AND t2.del_flag = '0' where 1=1
and t2.user_key = #{userKey,jdbcType=CHAR}
and t2.department_id = #{departmentId,jdbcType=CHAR}
ORDER BY t1.sort, t1.modify_time DESC

SELECT DISTINCT t1.company_id,company_name,t1.department_parent_id, t1.departmentFlag FROM sys_project AS t1 INNER JOIN sys_user_department AS t2 ON t2.department_id = t1.department_id AND t1.del_flag = '0' AND t2.del_flag = '0' where 1=1
and t2.user_key = #{userKey,jdbcType=CHAR}
and t2.department_id = #{departmentId,jdbcType=CHAR}
and t1.company_id = #{companyId,jdbcType=CHAR}
ORDER BY t1.sort, t1.modify_time DESC

SELECT t.* FROM ( SELECT department_id, department_name, project_id, project_name, company_id, company_name FROM sys_department WHERE del_flag = '0' AND department_flag = '2'
AND company_id = #{companyId,jdbcType=CHAR}
AND project_id = #{projectId,jdbcType=CHAR}
UNION ALL SELECT department_id, department_name, project_id, project_name, company_id, company_name FROM sys_project WHERE del_flag = '0' AND department_flag = '4'
AND company_id = #{companyId,jdbcType=CHAR}
AND project_id = #{projectId,jdbcType=CHAR}
) t
