SELECT t3.project_id, pro_no AS pro_num, t5.company_name AS comname, (SELECT d.label FROM sys_dict d inner join sys_dict_category c on c.id = d.category_id and c.del_flag = '0' inner join pro_inv_basic b on b.gclb = d.value and b.del_flag = '0' where d.del_flag = '0' and c.category_code = 'XM_GXLB' AND b.project_id = t3.project_id) AS category_name, t5.amount1 AS ztze, t5.amount3 AS jaf_money, t5.amount4 AS zcf_money, project_name AS pro_name, szgq, company8 AS ygjsgfe, (SELECT d.label FROM sys_dict d INNER JOIN sys_dict_category c ON c.id = d.category_id and c.del_flag = '0' INNER JOIN pro_inv_basic b ON b.is_report = d.value and b.del_flag = '0' where d.del_flag = '0' AND c.category_code = 'PFLX' and b.project_id = t3.project_id) AS pfname, t1.*, t5.amount3 * company8 as ygjjaf, (SELECT SUM(tb1.tzwcbq) FROM pro_inv_xmtzqk tb1 INNER JOIN pro_inv_basic tb2 ON tb2.id = tb1.inv_pro_id AND tb2.del_flag = '0' WHERE tb1.del_flag = '0' AND tb2.project_id = t3.project_id AND tb1.period_value IN #{model}) AS tzwcbj, (SELECT SUM(tb1.tzwcbq) FROM pro_inv_xmtzqk tb1 INNER JOIN pro_inv_basic tb2 ON tb2.id = tb1.inv_pro_id AND tb2.del_flag = '0' WHERE tb1.del_flag = '0' AND tb2.project_id = t3.project_id AND tb1.period_value IN #{model})*szgq/100 AS qytzwcbj, (SELECT SUM(tb1.wcjafbq) FROM pro_inv_xmtzqk tb1 INNER JOIN pro_inv_basic tb2 ON tb2.id = tb1.inv_pro_id AND tb2.del_flag = '0' WHERE tb1.del_flag = '0' AND tb2.project_id = t3.project_id AND tb1.period_value IN #{model}) AS wcjafbj, (SELECT SUM(tb1.xmztwcjlbq) FROM pro_inv_xmtzqk tb1 INNER JOIN pro_inv_basic tb2 ON tb2.id = tb1.inv_pro_id AND tb2.del_flag = '0' WHERE tb1.del_flag = '0' AND tb2.project_id = t3.project_id AND tb1.period_value IN #{model}) AS xmztwcjlbj, (SELECT SUM(tb1.zcjafbq) FROM pro_inv_xmtzqk tb1 INNER JOIN pro_inv_basic tb2 ON tb2.id = tb1.inv_pro_id AND tb2.del_flag = '0' WHERE tb1.del_flag = '0' AND tb2.project_id = t3.project_id AND tb1.period_value IN #{model}) AS zcjafbj, (SELECT SUM(tb1.ygjjtwcjafbq) FROM pro_inv_xmtzqk tb1 INNER JOIN pro_inv_basic tb2 ON tb2.id = tb1.inv_pro_id AND tb2.del_flag = '0' WHERE tb1.del_flag = '0' AND tb2.project_id = t3.project_id AND tb1.period_value IN #{model}) AS ygjjtwcjafbj, (SELECT SUM(tb1.ygjjtwcjlbq) FROM pro_inv_xmtzqk tb1 INNER JOIN pro_inv_basic tb2 ON tb2.id = tb1.inv_pro_id AND tb2.del_flag = '0' WHERE tb1.del_flag = '0' AND tb2.project_id = t3.project_id AND tb1.period_value IN #{model}) AS ygjjtwcjlbj, (SELECT SUM(tb1.ygjjtzcjafbq) FROM pro_inv_xmtzqk tb1 INNER JOIN pro_inv_basic tb2 ON tb2.id = tb1.inv_pro_id AND tb2.del_flag = '0' WHERE tb1.del_flag = '0' AND tb2.project_id = t3.project_id AND tb1.period_value IN #{model}) AS ygjjtzcjafbj, (SELECT SUM(tb1.wczcfbq) FROM pro_inv_xmtzqk tb1 INNER JOIN pro_inv_basic tb2 ON tb2.id = tb1.inv_pro_id AND tb2.del_flag = '0' WHERE tb1.del_flag = '0' AND tb2.project_id = t3.project_id AND tb1.period_value IN #{model}) AS wczcfbj, (SELECT SUM(tb1.zczcfbq) FROM pro_inv_xmtzqk tb1 INNER JOIN pro_inv_basic tb2 ON tb2.id = tb1.inv_pro_id AND tb2.del_flag = '0' WHERE tb1.del_flag = '0' AND tb2.project_id = t3.project_id AND tb1.period_value IN #{model}) AS zczcfbj FROM pro_inv_xmtzqk t1 INNER JOIN pro_inv_com_inf t2 ON t2.inv_pro_id = t1.inv_pro_id and t2.del_flag = '0' INNER JOIN pro_inv_basic t3 ON t3.id = t1.inv_pro_id and t3.del_flag = '0' INNER JOIN pro_inv_ht_info t4 ON t4.inv_pro_id = t1.inv_pro_id and t4.del_flag = '0' INNER JOIN zj_tz_pro_manage t5 ON t5.project_id = t3.project_id and t5.del_flag = '0'
t1.del_flag = '0'
and t1.period_value = #{param1.periodValue}
