,1 as dataType ,(SELECT p.bjqytzbfb FROM ( SELECT t.project_id, (SUM( t.tzwcbq ) * t.szgq / 100) / ( t.tzwc1 (t.tzwc1 + t.tzwc2) (t.tzwc1 + t.tzwc2 + t.tzwc3) (t.tzwc1 + t.tzwc2 + t.tzwc3 + t.tzwc4) * t.szgq / 100) AS bjqytzbfb FROM ( SELECT tb1.project_id, tb3.tzwcbq, tb4.szgq, tb5.tzwc1, tb5.tzwc1,tb5.tzwc2, tb5.tzwc1,tb5.tzwc2,tb5.tzwc3, tb5.tzwc1,tb5.tzwc2,tb5.tzwc3,tb5.tzwc4, QUARTER ( STR_TO_DATE( tb3.period_value, '%Y%m' ) ) AS jidu, YEAR ( STR_TO_DATE( tb3.period_value, '%Y' ) ) AS n, tb5.period_value FROM zj_tz_pro_manage tb1 LEFT JOIN pro_inv_basic tb2 ON tb2.project_id = tb1.project_id and tb2.del_flag = '0' LEFT JOIN pro_inv_xmtzqk tb3 ON tb3.inv_pro_id = tb2.id and tb3.del_flag = '0' LEFT JOIN pro_inv_com_inf tb4 ON tb4.inv_pro_id = tb2.id and tb4.del_flag = '0' LEFT JOIN pro_inv_xmndtzjh tb5 ON tb5.xmid = tb2.id and tb5.bbh = '2' WHERE tb1.del_flag = '0' AND tb1.project_type = '0' AND (tb1.pro_process_id = '2' or tb1.pro_process_id = '3' or tb1.pro_process_id = '4') ) AS t
