t.n = #{periodValue}
AND t.jidu <= #{quarter}
AND t.period_value = #{periodValue}
GROUP BY t.project_id ORDER BY bjjjafbfb DESC ) AS p where p.project_id = t3.project_id) AS bjjjafbfb
,3 as dataType ,(SELECT p.bnztzbfb FROM ( SELECT t.project_id, SUM( t.tzwcbq ) / (t.tzwc1 + t.tzwc2 + t.tzwc3 + t.tzwc4) AS bnztzbfb FROM ( SELECT tb1.project_id, tb3.tzwcbq, tb4.szgq, tb5.tzwc1,tb5.tzwc2,tb5.tzwc3,tb5.tzwc4, YEAR ( STR_TO_DATE( tb3.period_value, '%Y' ) ) AS n, tb5.period_value FROM zj_tz_pro_manage tb1 LEFT JOIN pro_inv_basic tb2 ON tb2.project_id = tb1.project_id and tb2.del_flag = '0' LEFT JOIN pro_inv_xmtzqk tb3 ON tb3.inv_pro_id = tb2.id and tb3.del_flag = '0' LEFT JOIN pro_inv_com_inf tb4 ON tb4.inv_pro_id = tb2.id and tb4.del_flag = '0' LEFT JOIN pro_inv_xmndtzjh tb5 ON tb5.xmid = tb2.id and tb5.bbh = '2' WHERE tb1.del_flag = '0' AND tb1.project_type = '0' AND (tb1.pro_process_id = '2' or tb1.pro_process_id = '3' or tb1.pro_process_id = '4') ) AS t
t.n = #{periodValue}
AND t.period_value = #{periodValue}
GROUP BY t.project_id ORDER BY bnztzbfb DESC ) AS p where p.project_id = t3.project_id) AS bnztzbfb
