select.if:
SELECT 1 as dataType, t.project_id, t.project_short_name AS short_name, (SUM( t.tzwcbq ) * t.szgq / 100) / ( IFNULL(t.tzwc1,0) (IFNULL(t.tzwc1,0) + IFNULL(t.tzwc2,0)) (IFNULL(t.tzwc1,0) + IFNULL(t.tzwc2,0) + IFNULL(t.tzwc3,0)) (IFNULL(t.tzwc1,0) + IFNULL(t.tzwc2,0) + IFNULL(t.tzwc3,0) + IFNULL(t.tzwc4,0)) * t.szgq / 100) AS bjqytzbfb, RANK() OVER(ORDER BY (SUM( IFNULL(t.tzwcbq,0) ) * IFNULL(t.szgq,0) / 100 )/
( IFNULL(t.tzwc1,0) (IFNULL(t.tzwc1,0) + IFNULL(t.tzwc2,0)) (IFNULL(t.tzwc1,0) + IFNULL(t.tzwc2,0) + IFNULL(t.tzwc3,0)) (IFNULL(t.tzwc1,0) + IFNULL(t.tzwc2,0) + IFNULL(t.tzwc3,0) + IFNULL(t.tzwc4,0)) * IFNULL(t.szgq,0) / 100) DESC) AS bjqytzpm FROM ( SELECT tb1.project_id, tb1.project_short_name, tb3.tzwcbq, tb4.szgq, tb5.tzwc1, tb5.tzwc1,tb5.tzwc2, tb5.tzwc1,tb5.tzwc2,tb5.tzwc3, tb5.tzwc1,tb5.tzwc2,tb5.tzwc3,tb5.tzwc4, QUARTER ( STR_TO_DATE( tb3.period_value, '%Y%m' ) ) AS jidu, YEAR ( STR_TO_DATE( tb3.period_value, '%Y' ) ) AS n, tb5.period_value FROM zj_tz_pro_manage tb1 LEFT JOIN pro_inv_basic tb2 ON tb2.project_id = tb1.project_id and tb2.del_flag = '0' LEFT JOIN pro_inv_xmtzqk tb3 ON tb3.inv_pro_id = tb2.id and tb3.del_flag = '0' LEFT JOIN pro_inv_com_inf tb4 ON tb4.inv_pro_id = tb2.id and tb4.del_flag = '0' LEFT JOIN pro_inv_xmndtzjh tb5 ON tb5.xmid = tb2.id and tb5.bbh = '2'
tb1.del_flag = '0' AND tb1.project_type = '0' AND (tb1.pro_process_id = '2' or tb1.pro_process_id = '3' or tb1.pro_process_id = '4')
AND tb1.project_id = #{projectId}
AND tb1.project_id in (${_parameter.projectIdSql})
) AS t
AND t.n = #{periodValue}
AND t.jidu <= #{quarter}
AND t.period_value = #{periodValue}
GROUP BY t.project_id HAVING bjqytzbfb IS NOT NULL ORDER BY bjqytzbfb DESC

