AND tb2.project_id in (${projectIdSql})
AND tb1.period_value BETWEEN CONCAT( date_format(DATE_SUB(curdate(), INTERVAL 1 YEAR), '%Y'), '01' ) and CONCAT( date_format(DATE_SUB(curdate(), INTERVAL 1 YEAR), '%Y'), date_format(DATE_SUB(curdate(), INTERVAL 1 MONTH), '%m') )
) ) - 1 AS tzwcbhl, TRUNCATE(SUM( wcjafbq )/10000,2) AS zwcjafbn, ( SUM( wcjafbq ) / ( SELECT SUM( wcjafbq ) FROM pro_inv_xmtzqk tb1 LEFT JOIN pro_inv_basic tb2 ON tb2.id = tb1.inv_pro_id and tb2.del_flag = '0' LEFT JOIN zj_tz_pro_manage tb3 ON tb3.project_id = tb2.project_id and tb3.del_flag = '0' and tb3.project_type = '0'
tb1.del_flag = '0'
AND tb2.project_id = #{projectId}
AND tb2.project_id in (${projectIdSql})
AND tb1.period_value BETWEEN CONCAT( date_format(DATE_SUB(curdate(), INTERVAL 1 YEAR), '%Y'), '01' ) and CONCAT( date_format(DATE_SUB(curdate(), INTERVAL 1 YEAR), '%Y'), date_format(DATE_SUB(curdate(), INTERVAL 1 MONTH), '%m') )
) ) - 1 AS wcjafbhl FROM pro_inv_xmtzqk t1 LEFT JOIN pro_inv_basic t2 ON t2.id = t1.inv_pro_id and t2.del_flag = '0' LEFT JOIN zj_tz_pro_manage t3 ON t3.project_id = t2.project_id and t3.del_flag = '0' and t3.project_type = '0'
t1.del_flag = '0'
AND t3.project_id = #{projectId}
AND t3.project_id in (${projectIdSql})
AND t1.period_value BETWEEN CONCAT(#{periodValue},'01') and CONCAT(#{periodValue},date_format(DATE_SUB(curdate(), INTERVAL 1 MONTH), '%m'))

SELECT t1.project_id, TRUNCATE(SUM( t3.tzwcbq )/10000,2) AS tzwcbj, TRUNCATE(SUM( t3.wcjafbq )/10000,2) AS wcjafbj, SUBSTR( t3.period_value, 1, 4 ) AS n, QUARTER ( STR_TO_DATE( concat( t3.period_value, '01' ), '%Y%m%d' ) ) AS jidu FROM zj_tz_pro_manage t1 LEFT JOIN pro_inv_basic t2 ON t2.project_id = t1.project_id AND t2.del_flag = '0' LEFT JOIN pro_inv_xmtzqk t3 ON t3.inv_pro_id = t2.id AND t3.del_flag = '0'
t1.del_flag = '0' and t1.project_type = '0'
and t1.project_id = #{projectId}
and SUBSTR( t3.period_value, 1, 4 ) = #{periodValue}
GROUP BY jidu ORDER BY jidu

SELECT t1.project_id, TRUNCATE(SUM( t3.tzwcbq )/10000,2) AS tzwckl, TRUNCATE(SUM( t3.wcjafbq )/10000,2) AS wcjafkl, SUBSTR( t3.period_value, 1, 4 ) AS n FROM zj_tz_pro_manage t1 LEFT JOIN pro_inv_basic t2 ON t2.project_id = t1.project_id AND t2.del_flag = '0' LEFT JOIN pro_inv_xmtzqk t3 ON t3.inv_pro_id = t2.id AND t3.del_flag = '0'
t1.del_flag = '0' and t1.project_type = '0'
and t1.project_id = #{projectId}
and SUBSTR( t3.period_value, 1, 4 ) <= #{periodValue}

select.if:
SELECT p.sjqytzpm FROM ( SELECT t.project_id, (SUM( t.tzwcbq ) * t.szgq / 100) / ( IFNULL(t.tzwc1,0) (IFNULL(t.tzwc1,0) + IFNULL(t.tzwc2,0)) (IFNULL(t.tzwc1,0) + IFNULL(t.tzwc2,0) + IFNULL(t.tzwc3,0)) * t.szgq / 100) AS sjqytzbfb, RANK() OVER(ORDER BY (SUM( IFNULL(t.tzwcbq,0) ) * IFNULL(t.szgq,0) / 100 )/
( IFNULL(t.tzwc1,0) (IFNULL(t.tzwc1,0) + IFNULL(t.tzwc2,0)) (IFNULL(t.tzwc1,0) + IFNULL(t.tzwc2,0) + IFNULL(t.tzwc3,0)) * IFNULL(t.szgq,0) / 100) DESC) AS sjqytzpm FROM ( SELECT tb1.project_id, tb3.tzwcbq, tb4.szgq, tb5.tzwc1, tb5.tzwc1,tb5.tzwc2, tb5.tzwc1,tb5.tzwc2, tb5.tzwc3, QUARTER ( STR_TO_DATE( tb3.period_value, '%Y%m' ) ) AS jidu, YEAR ( STR_TO_DATE( tb3.period_value, '%Y' ) ) AS n, tb5.period_value FROM zj_tz_pro_manage tb1 LEFT JOIN pro_inv_basic tb2 ON tb2.project_id = tb1.project_id and tb2.del_flag = '0' LEFT JOIN pro_inv_xmtzqk tb3 ON tb3.inv_pro_id = tb2.id and tb3.del_flag = '0' LEFT JOIN pro_inv_com_inf tb4 ON tb4.inv_pro_id = tb2.id and tb4.del_flag = '0' LEFT JOIN pro_inv_xmndtzjh tb5 ON tb5.xmid = tb2.id and tb5.bbh = '2'
tb1.del_flag = '0' AND tb1.project_type = '0' AND (tb1.pro_process_id = '2' or tb1.pro_process_id = '3' or tb1.pro_process_id = '4') ) AS t
AND t.n = #{periodValue}
AND t.jidu <= CAST(#{quarter} AS SIGNED) - 1
AND t.period_value = #{periodValue}
GROUP BY t.project_id HAVING sjqytzbfb IS NOT NULL ORDER BY sjqytzbfb DESC ) AS p
and p.project_id = #{projectId}

SELECT p.sjjjafpm FROM ( SELECT t.project_id, SUM( t.ygjjtwcjafbq ) / t.yjgjtwcja1 (t.yjgjtwcja1 + t.yjgjtwcja2) (t.yjgjtwcja1 + t.yjgjtwcja2 + t.yjgjtwcja3) AS sjjjafbfb, RANK() OVER(ORDER BY SUM( t.ygjjtwcjafbq )/ t.yjgjtwcja1 (t.yjgjtwcja1 + t.yjgjtwcja2) (t.yjgjtwcja1 + t.yjgjtwcja2 + t.yjgjtwcja3) DESC) AS sjjjafpm FROM ( SELECT tb1.project_id, tb3.ygjjtwcjafbq, tb4.szgq, tb5.yjgjtwcja1, tb5.yjgjtwcja1,tb5.yjgjtwcja2, tb5.yjgjtwcja1,tb5.yjgjtwcja2,tb5.yjgjtwcja3, QUARTER ( STR_TO_DATE( tb3.period_value, '%Y%m' ) ) AS jidu, YEAR ( STR_TO_DATE( tb3.period_value, '%Y' ) ) AS n, tb5.period_value FROM zj_tz_pro_manage tb1 LEFT JOIN pro_inv_basic tb2 ON tb2.project_id = tb1.project_id and tb2.del_flag = '0' LEFT JOIN pro_inv_xmtzqk tb3 ON tb3.inv_pro_id = tb2.id and tb3.del_flag = '0' LEFT JOIN pro_inv_com_inf tb4 ON tb4.inv_pro_id = tb2.id and tb4.del_flag = '0' LEFT JOIN pro_inv_xmndtzjh tb5 ON tb5.xmid = tb2.id and tb5.bbh = '2'
tb1.del_flag = '0' AND tb1.project_type = '0' AND (tb1.pro_process_id = '2' or tb1.pro_process_id = '3' or tb1.pro_process_id = '4') ) AS t
AND t.n = #{periodValue}
AND t.jidu <= CAST(#{quarter} AS SIGNED) - 1
AND t.period_value = #{periodValue}
GROUP BY t.project_id HAVING sjjjafbfb IS NOT NULL ORDER BY sjjjafbfb DESC )AS p
and p.project_id = #{projectId}
