mapper.delete:
delete from zj_xm_jx_assessment_user_score
where del_flag = '0'

 and assessment_id = #{assessmentId,jdbcType=CHAR}


 and assessment_type = #{assessmentType,jdbcType=CHAR}


 and reviewer_key = #{reviewerKey,jdbcType=CHAR}

mapper.update:
