mapper.select:
select 
    t_score.*,
    t_asst.year_month as yearMonth,
    t_asst.assessment_title as assessmentTitle
from zj_xm_jx_assessment_user_score t_score INNER JOIN zj_xm_jx_monthly_assessment t_asst
                                on t_score.assessment_id = t_asst.assessment_id
where t_score.del_flag = '0' and t_asst.del_flag = '0'

 and t_score.assessment_id = #{assessmentId,jdbcType=CHAR}


 and t_score.auditee_key = #{auditeeKey,jdbcType=CHAR}


 and t_score.auditee_name = #{auditeeName,jdbcType=VARCHAR}


 and t_score.auditee_id_number = #{auditeeIdNumber,jdbcType=VARCHAR}


 and t_score.auditee_position = #{auditeePosition,jdbcType=CHAR}


 and t_score.auditee_rank_type = #{auditeeRankType,jdbcType=CHAR}


 and t_score.auditee_last_type = #{auditeeLastType,jdbcType=CHAR}


 and t_score.auditee_user_type = #{auditeeUserType,jdbcType=CHAR}


 and t_score.auditee_type = #{auditeeType,jdbcType=CHAR}


 and t_score.auditee_dept_id = #{auditeeDeptId,jdbcType=CHAR}


 and t_score.auditee_dept_name = #{auditeeDeptName,jdbcType=VARCHAR}


 and t_score.auditee_pro_id = #{auditeeProId,jdbcType=CHAR}


 and t_score.auditee_pro_name = #{auditeeProName,jdbcType=VARCHAR}


 and t_score.assessment_type = #{assessmentType,jdbcType=CHAR}


 and t_score.confirm_status = #{confirmStatus,jdbcType=CHAR}


 and t_score.notified_time = #{notifiedTime,jdbcType=TIMESTAMP}


 and t_score.score = #{score,jdbcType=DECIMAL}


 and t_score.principal_score = #{principalScore,jdbcType=DECIMAL}


 and t_score.secretary_score = #{secretaryScore,jdbcType=DECIMAL}


 and t_score.reviewer_position = #{reviewerPosition,jdbcType=CHAR}


 and t_score.reviewer_key = #{reviewerKey,jdbcType=VARCHAR}


 and t_score.reviewer_name = #{reviewerName,jdbcType=VARCHAR}


 and t_score.audit_time = #{auditTime,jdbcType=TIMESTAMP}


 and t_score.audit_status = #{auditStatus,jdbcType=CHAR}


 and t_score.remarks = #{remarks,jdbcType=VARCHAR}


 and t_asst.notice_status = #{noticeStatus,jdbcType=CHAR}


 and t_asst.assessment_title like CONCAT('%', #{assessmentTitle,jdbcType=VARCHAR}, '%')


 and date_format(t_asst.`year_month`, '%Y-%m') = date_format(#{yearMonth,jdbcType=TIMESTAMP},'%Y-%m')

ORDER BY t_score.score ASC

 select 
    t_score.*,
    t_asst.year_month as yearMonth,
    t_asst.assessment_title as assessmentTitle,
    CONCAT(COUNT(IF(t_score.assessment_type = '0' and t_score.audit_status != '4',TRUE,NULL)),'/',
           COUNT(IF(t_score.assessment_type = '0' and t_score.audit_status = '4',TRUE,NULL))) as taskRatio,
    (SELECT IF(COUNT(1) > 0, '未评分', '已评分') FROM zj_xm_jx_periphery_score_detailed t_det 
                          WHERE t_det.del_flag = 0
                            AND t_det.assessment_id = t_score.assessment_id
                            AND t_det.reviewer_key = #{reviewerKey,jdbcType=VARCHAR} 
                            AND t_det.audit_status != '4')  as peripheryComp,
    (SELECT IF(COUNT(1) > 0, '未评分', '已评分') FROM zj_xm_jx_principal_score_detailed t_det 
                          WHERE t_det.del_flag = 0
                            AND t_det.assessment_id = t_score.assessment_id
                            AND t_det.reviewer_key = #{reviewerKey,jdbcType=VARCHAR} 
                            AND t_det.audit_status != '4') as principalComp,
    (select IF(COUNT(1) > 0, '已评分', '评分') from zj_xm_jx_assessment_user_score t2
       where t2.del_flag = '0' and t2.assessment_type = '3'
         and t2.audit_status = 4
         and t2.assessment_id = t_score.assessment_id
         and t2.reviewer_key = #{reviewerKey,jdbcType=VARCHAR}) as compositeComp
from zj_xm_jx_assessment_user_score t_score INNER JOIN zj_xm_jx_monthly_assessment t_asst
                                on t_score.assessment_id = t_asst.assessment_id
where t_score.del_flag = '0' and t_asst.del_flag = '0'

 and t_score.assessment_id = #{assessmentId,jdbcType=CHAR}


 and t_score.auditee_key = #{auditeeKey,jdbcType=CHAR}


 and t_score.auditee_name = #{auditeeName,jdbcType=VARCHAR}


 and t_score.auditee_id_number = #{auditeeIdNumber,jdbcType=VARCHAR}


 and t_score.auditee_position = #{auditeePosition,jdbcType=CHAR}


 and t_score.auditee_rank_type = #{auditeeRankType,jdbcType=CHAR}


 and t_score.auditee_last_type = #{auditeeLastType,jdbcType=CHAR}


 and t_score.auditee_user_type = #{auditeeUserType,jdbcType=CHAR}


 and t_score.auditee_type = #{auditeeType,jdbcType=CHAR}


 and t_score.auditee_dept_id = #{auditeeDeptId,jdbcType=CHAR}


 and t_score.auditee_dept_name = #{auditeeDeptName,jdbcType=VARCHAR}


 and t_score.auditee_pro_id = #{auditeeProId,jdbcType=CHAR}


 and t_score.auditee_pro_name = #{auditeeProName,jdbcType=VARCHAR}


 and t_score.assessment_type = #{assessmentType,jdbcType=CHAR}


 and t_score.confirm_status = #{confirmStatus,jdbcType=CHAR}


 and t_score.notified_time = #{notifiedTime,jdbcType=TIMESTAMP}


 and t_score.score = #{score,jdbcType=DECIMAL}


 and t_score.principal_score = #{principalScore,jdbcType=DECIMAL}


 and t_score.secretary_score = #{secretaryScore,jdbcType=DECIMAL}


 AND  t_score.reviewer_position = #{reviewerPosition,jdbcType=CHAR}


 AND  t_score.reviewer_key LIKE CONCAT('%', #{reviewerKey,jdbcType=VARCHAR}, '%')


 and t_score.reviewer_name = #{reviewerName,jdbcType=VARCHAR}


 and t_score.audit_time = #{auditTime,jdbcType=TIMESTAMP}


 and t_score.audit_status = #{auditStatus,jdbcType=CHAR}


 and t_score.remarks = #{remarks,jdbcType=VARCHAR}


 and t_asst.notice_status = #{noticeStatus,jdbcType=CHAR}


 and t_asst.assessment_title like CONCAT('%', #{assessmentTitle,jdbcType=VARCHAR}, '%')


 and date_format(t_asst.`year_month`, '%Y-%m') = date_format(#{yearMonth,jdbcType=TIMESTAMP},'%Y-%m')

GROUP BY t_score.assessment_id 
ORDER BY t_score.modify_time DESC


  select 
    t_score.*,
    t_asst.year_month as yearMonth,
    t_asst.assessment_title as assessmentTitle,
    MAX(CASE t_score.assessment_type WHEN '0' THEN t_score.score ELSE 0 END ) taskTotalScore, 
    MAX(CASE t_score.assessment_type WHEN '1' THEN t_score.score ELSE 0 END ) peripheryTotalScore,
    MAX(CASE t_score.assessment_type WHEN '2' THEN t_score.score ELSE 0 END ) principalTotalScore,
    MAX(CASE t_score.assessment_type WHEN '3' THEN -t_score.score ELSE 0 END ) compositeTotalScore,
    MAX(CASE t_score.assessment_type WHEN '4' THEN -t_score.score ELSE 0 END ) systemTotalScore,
    CONCAT(MAX(CASE t_score.assessment_type WHEN '0' THEN t_score.score ELSE 0 END),'(',IFNULL(t_man.task_weight,'未设置'),')') as taskStr,
    CONCAT(MAX(CASE t_score.assessment_type WHEN '1' THEN t_score.score ELSE 0 END),'(',IFNULL(t_man.periphery_weight,'未设置'),')') as peripheryStr,
    CONCAT(MAX(CASE t_score.assessment_type WHEN '2' THEN t_score.score ELSE 0 END),'(',IFNULL(t_man.principal_weight,'未设置'),')') as principalStr,
    (SELECT rankNo FROM
           (SELECT (@rankNo := @rankNo+1) AS rankNo ,new1.* from
           (SELECT t1.auditee_key, t1.auditee_name, t1.score      
            FROM `zj_xm_jx_assessment_user_score` t1 
            WHERE t1.del_flag = '0' and t1.assessment_type = '0'
              AND t1.assessment_id =  t_asst.assessment_id
            GROUP BY t1.auditee_key
            ORDER BY t1.score DESC
           )as new1, 
           (select @rankNo:=0 )as new2
           )as new3 WHERE new3.auditee_key = t_score.auditee_key) as rankNo
from zj_xm_jx_assessment_user_score t_score INNER JOIN zj_xm_jx_monthly_assessment t_asst
                                on t_score.assessment_id = t_asst.assessment_id
                                            LEFT JOIN zj_xm_jx_weight_management t_man
                                ON  date_format(t_asst.year_month, '%Y-%m') = date_format(t_man.year_month,'%Y-%m')
                                            and t_man.del_flag = '0'
where t_score.del_flag = '0' and t_asst.del_flag = '0'

 and t_score.assessment_id = #{assessmentId,jdbcType=CHAR}


 and t_score.auditee_key = #{auditeeKey,jdbcType=CHAR}


 and t_score.auditee_name = #{auditeeName,jdbcType=VARCHAR}


 and t_score.auditee_id_number = #{auditeeIdNumber,jdbcType=VARCHAR}


 and t_score.auditee_position = #{auditeePosition,jdbcType=CHAR}


 and t_score.auditee_rank_type = #{auditeeRankType,jdbcType=CHAR}


 and t_score.auditee_last_type = #{auditeeLastType,jdbcType=CHAR}


 and t_score.auditee_user_type = #{auditeeUserType,jdbcType=CHAR}


 and t_score.auditee_type = #{auditeeType,jdbcType=CHAR}


 and t_score.auditee_dept_id = #{auditeeDeptId,jdbcType=CHAR}


 and t_score.auditee_dept_name = #{auditeeDeptName,jdbcType=VARCHAR}


 and t_score.auditee_pro_id = #{auditeeProId,jdbcType=CHAR}


 and t_score.auditee_pro_name = #{auditeeProName,jdbcType=VARCHAR}


 and t_score.assessment_type = #{assessmentType,jdbcType=CHAR}


 and t_score.confirm_status = #{confirmStatus,jdbcType=CHAR}


 and t_score.notified_time = #{notifiedTime,jdbcType=TIMESTAMP}


 and t_score.score = #{score,jdbcType=DECIMAL}


 and t_score.principal_score = #{principalScore,jdbcType=DECIMAL}


 and t_score.secretary_score = #{secretaryScore,jdbcType=DECIMAL}


 and t_score.reviewer_position = #{reviewerPosition,jdbcType=CHAR}


 and t_score.reviewer_key = #{reviewerKey,jdbcType=VARCHAR}


 and t_score.reviewer_name = #{reviewerName,jdbcType=VARCHAR}


 and t_score.audit_time = #{auditTime,jdbcType=TIMESTAMP}


 and t_score.audit_status = #{auditStatus,jdbcType=CHAR}


 and t_score.deduct_reason = #{deductReason,jdbcType=VARCHAR}


 and t_score.remarks = #{remarks,jdbcType=VARCHAR}

 GROUP BY t_score.auditee_key, t_score.assessment_id 

 ORDER BY t_asst.year_month DESC

mapper.update:
update zj_xm_jx_assessment_user_score
confirm_status = '2', audit_status = '2',
where del_flag = '0' and assessment_type = '0' and confirm_status = '0' and notified_time is not null and TIMESTAMPDIFF(MINUTE, notified_time, NOW()) > #{startValue,jdbcType=INTEGER}

mapper.select:
select count(1) from zj_xm_jx_assessment_user_score where del_flag = '0' and assessment_type = '3' and audit_status = 4
and assessment_id = #{assessmentId,jdbcType=CHAR}
and reviewer_key = #{reviewerKey,jdbcType=CHAR}

select t_score.*, t_asst.year_month as yearMonth, t_asst.assessment_title as assessmentTitle from zj_xm_jx_assessment_user_score t_score INNER JOIN zj_xm_jx_monthly_assessment t_asst on t_score.assessment_id = t_asst.assessment_id where t_score.del_flag = '0' and t_asst.del_flag = '0' and t_score.notified_time is not null and TIMESTAMPDIFF(MINUTE, t_score.notified_time, NOW()) > #{startValue,jdbcType=INTEGER} and TIMESTAMPDIFF(MINUTE, t_score.notified_time, NOW()) < #{endValue,jdbcType=INTEGER} and t_score.audit_status != 4
and t_score.assessment_type = #{assessmentType,jdbcType=CHAR}
ORDER BY t_score.modify_time DESC

mapper.update:
update zj_xm_jx_assessment_user_score
score = 0, audit_status = '4', audit_time = now(),
where del_flag = '0' and score_id in #{model.scoreId}

update zj_xm_jx_assessment_user_score
score = score - 10, audit_status = '4', audit_time = now(),
where del_flag = '0' and assessment_type = '4'
and assessment_id = #{assessmentId,jdbcType=CHAR}
and auditee_key = #{auditeeKey,jdbcType=CHAR}

update zj_xm_jx_assessment_user_score
score = null, audit_status = '2',
where del_flag = '0' and score_id in #{model.scoreId}
