mapper.update:
update zj_xm_jx_quarterly_project_details
confirm_status = '1',
where del_flag = '0' and details_id in #{model.detailsId}

mapper.select:
 SELECT  
      t1.details_id AS detailsId,
      t1.assessment_id AS assessmentId,
      t1.project_id AS projectId, 
      t1.project_name AS projectName, 
      t1.dept_id AS deptId,
      t1.dept_name AS deptName,
      t1.project_status AS projectStatus,
      t1.project_status_name AS projectStatusName,
      t1.actual_status AS actualStatus,
      t1.actual_status_name AS actualStatusName,
      IFNULL(sum(t1.actual_score),0) AS totalScore,


             MAX(CASE t1.library_id WHEN #{item.libraryId} THEN IFNULL(t1.actual_score, t1.actual_score) 
                          ELSE ''  END ) #{item.libraryId}

         ,

      t1.manager_key  AS managerKey,
       t1.manager_name AS managerName
      FROM `zj_xm_jx_quarterly_project_details`  t1             
      WHERE t1.del_flag = '0' 

 and t1.details_id = #{detailsId,jdbcType=CHAR}


 and t1.assessment_id = #{assessmentId,jdbcType=CHAR}


 and t1.project_id = #{projectId,jdbcType=CHAR}


 and t1.project_name = #{projectName,jdbcType=VARCHAR}


 and t1.manager_key = #{managerKey,jdbcType=CHAR}


 and t1.manager_name = #{managerName,jdbcType=VARCHAR}


 and t1.actual_status = #{actualStatus,jdbcType=CHAR}


 and t1.actual_status_name = #{actualStatusName,jdbcType=VARCHAR}


 and t1.actual_type = #{actualType,jdbcType=CHAR}


 and t1.actual_type_name = #{actualTypeName,jdbcType=VARCHAR}


 and t1.actual_score = #{actualScore,jdbcType=DECIMAL}


 and t1.library_id = #{libraryId,jdbcType=CHAR}


 and t1.dept_id = #{deptId,jdbcType=CHAR}


 and t1.dept_name = #{deptName,jdbcType=VARCHAR}


 and t1.project_status = #{projectStatus,jdbcType=CHAR}


 and t1.project_status_name = #{projectStatusName,jdbcType=VARCHAR}


 and t1.project_type = #{projectType,jdbcType=CHAR}


 and t1.project_type_name = #{projectTypeName,jdbcType=VARCHAR}


 and t1.library_title = #{libraryTitle,jdbcType=VARCHAR}


 and t1.library_content = #{libraryContent,jdbcType=VARCHAR}


 and t1.person_liable_key = #{personLiableKey,jdbcType=CHAR}


 and t1.person_liable_name = #{personLiableName,jdbcType=VARCHAR}


 and t1.is_fixed_score = #{isFixedScore,jdbcType=CHAR}


 and t1.score = #{score,jdbcType=DECIMAL}


 and t1.lower_limit_score = #{lowerLimitScore,jdbcType=DECIMAL}


 and t1.upper_limit_score = #{upperLimitScore,jdbcType=DECIMAL}


 and t1.is_closed = #{isClosed,jdbcType=CHAR}


 and t1.confirm_status = #{confirmStatus,jdbcType=CHAR}


 and t1.remarks = #{remarks,jdbcType=VARCHAR}

GROUP BY t1.project_id

update.foreach:

update zj_xm_jx_quarterly_project_details

         actual_score = #{item.actualScore,jdbcType=DECIMAL},

         modify_time = #{item.modifyTime,jdbcType=TIMESTAMP},
         modify_user = #{item.modifyUser,jdbcType=CHAR},
         modify_user_name = #{item.modifyUserName,jdbcType=VARCHAR}

       where dept_id = #{item.deptId,jdbcType=CHAR}
         and project_id = #{item.projectId,jdbcType=CHAR}
         and library_id = #{item.libraryId,jdbcType=CHAR}
         and assessment_id = #{item.assessmentId,jdbcType=CHAR}

mapper.select:
  SELECT (@rankNo := @rankNo+1) AS rankNo,
          new1.* 
    FROM  
 (SELECT  
      t1.details_id AS detailsId,
      t1.assessment_id AS assessmentId,
      t1.project_id AS projectId, 
      t1.project_name AS projectName, 
      t1.dept_id AS deptId,
      t1.dept_name AS deptName,
      t1.project_status AS projectStatus,
      t1.project_status_name AS projectStatusName,
      t1.actual_status AS actualStatus,
      t1.actual_status_name AS actualStatusName,
      IFNULL(sum(t1.actual_score),0) AS totalScore,


             MAX(CASE t1.library_id WHEN #{item.libraryId} THEN IFNULL(t1.actual_score, '未打分') 
                          ELSE ''  END ) #{item.libraryId}

         ,

      t1.manager_key  AS managerKey,
       t1.manager_name AS managerName
      FROM `zj_xm_jx_quarterly_project_details`  t1             
      WHERE t1.del_flag = '0' 

 and t1.details_id = #{detailsId,jdbcType=CHAR}


 and t1.assessment_id = #{assessmentId,jdbcType=CHAR}


 and t1.project_id = #{projectId,jdbcType=CHAR}


 and t1.project_name = #{projectName,jdbcType=VARCHAR}


 and t1.manager_key = #{managerKey,jdbcType=CHAR}


 and t1.manager_name = #{managerName,jdbcType=VARCHAR}


 and t1.actual_status = #{actualStatus,jdbcType=CHAR}


 and t1.actual_status_name = #{actualStatusName,jdbcType=VARCHAR}


 and t1.actual_type = #{actualType,jdbcType=CHAR}


 and t1.actual_type_name = #{actualTypeName,jdbcType=VARCHAR}


 and t1.actual_score = #{actualScore,jdbcType=DECIMAL}


 and t1.library_id = #{libraryId,jdbcType=CHAR}


 and t1.dept_id = #{deptId,jdbcType=CHAR}


 and t1.dept_name = #{deptName,jdbcType=VARCHAR}


 and t1.project_status = #{projectStatus,jdbcType=CHAR}


 and t1.project_status_name = #{projectStatusName,jdbcType=VARCHAR}


 and t1.project_type = #{projectType,jdbcType=CHAR}


 and t1.project_type_name = #{projectTypeName,jdbcType=VARCHAR}


 and t1.library_title = #{libraryTitle,jdbcType=VARCHAR}


 and t1.library_content = #{libraryContent,jdbcType=VARCHAR}


 and t1.person_liable_key = #{personLiableKey,jdbcType=CHAR}


 and t1.person_liable_name = #{personLiableName,jdbcType=VARCHAR}


 and t1.is_fixed_score = #{isFixedScore,jdbcType=CHAR}


 and t1.score = #{score,jdbcType=DECIMAL}


 and t1.lower_limit_score = #{lowerLimitScore,jdbcType=DECIMAL}


 and t1.upper_limit_score = #{upperLimitScore,jdbcType=DECIMAL}


 and t1.is_closed = #{isClosed,jdbcType=CHAR}


 and t1.confirm_status = #{confirmStatus,jdbcType=CHAR}


 and t1.remarks = #{remarks,jdbcType=VARCHAR}

GROUP BY t1.project_id
) AS new1, (select @rankNo:=0)as new2

