SELECT (@rankNo := @rankNo+1) AS rankNo,
          new1.* 
    FROM  
 (SELECT 
      deptCount.details_id AS detailsId,
      deptCount.assessment_id AS assessmentId,
      deptCount.project_id AS projectId, 
      deptCount.project_name AS projectName, 
      deptCount.dept_id AS deptId,
      deptCount.dept_name AS deptName,
      deptCount.project_status AS projectStatus,
      deptCount.project_status_name AS projectStatusName,
      deptCount.actual_status AS actualStatus,
      deptCount.actual_status_name AS actualStatusName,
      SUM(deptCount.deptScore) as totalScore,


             MAX(CASE deptCount.dept_name WHEN #{item.deptName} THEN IFNULL(deptCount.deptScore, '未打分') 
                          ELSE ''  END ) #{item.deptName}

         ,

 deptCount.manager_key  AS managerKey,
  deptCount.manager_name AS managerName
  FROM (
      SELECT *, SUM(score) AS deptScore
      FROM zj_xm_jx_quarterly_project_details
where del_flag = '0' 

 and details_id = #{detailsId,jdbcType=CHAR}


 and assessment_id = #{assessmentId,jdbcType=CHAR}


 and project_id = #{projectId,jdbcType=CHAR}


 and project_name = #{projectName,jdbcType=VARCHAR}


 and manager_key = #{managerKey,jdbcType=CHAR}


 and manager_name = #{managerName,jdbcType=VARCHAR}


 and actual_status = #{actualStatus,jdbcType=CHAR}


 and actual_status_name = #{actualStatusName,jdbcType=VARCHAR}


 and actual_type = #{actualType,jdbcType=CHAR}


 and actual_type_name = #{actualTypeName,jdbcType=VARCHAR}


 and actual_score = #{actualScore,jdbcType=DECIMAL}


 and library_id = #{libraryId,jdbcType=CHAR}


 and dept_id = #{deptId,jdbcType=CHAR}


 and dept_name = #{deptName,jdbcType=VARCHAR}


 and project_status = #{projectStatus,jdbcType=CHAR}


 and project_status_name = #{projectStatusName,jdbcType=VARCHAR}


 and project_type = #{projectType,jdbcType=CHAR}


 and project_type_name = #{projectTypeName,jdbcType=VARCHAR}


 and library_title = #{libraryTitle,jdbcType=VARCHAR}


 and library_content = #{libraryContent,jdbcType=VARCHAR}


 and person_liable_key = #{personLiableKey,jdbcType=CHAR}


 and person_liable_name = #{personLiableName,jdbcType=VARCHAR}


 and is_fixed_score = #{isFixedScore,jdbcType=CHAR}


 and score = #{score,jdbcType=DECIMAL}


 and lower_limit_score = #{lowerLimitScore,jdbcType=DECIMAL}


 and upper_limit_score = #{upperLimitScore,jdbcType=DECIMAL}


 and is_closed = #{isClosed,jdbcType=CHAR}


 and confirm_status = #{confirmStatus,jdbcType=CHAR}


 and remarks = #{remarks,jdbcType=VARCHAR}

GROUP BY project_id, dept_id
) as deptCount
where deptCount.del_flag = '0' GROUP BY deptCount.project_id
) AS new1, (select @rankNo:=0)as new2

