SELECT 
   t_user.*,

   (SELECT department_name FROM sys_department t_dept WHERE t_dept.del_flag = '0'
                           AND t_dept.department_id = t_user.project_id) as projectName,

   (SELECT department_name FROM sys_department t_dept WHERE t_dept.del_flag = '0'
                           AND t_dept.department_id = t_user.office_id) as officeName,
   (SELECT department_name FROM sys_department t_dept WHERE t_dept.del_flag = '0'
                           AND t_dept.department_id = t_user.wage_of_project_id) as wageProjectName
    FROM 
         zj_xm_salary_user_extension t_user 

where t_user.del_flag = '0'

 and t_user.user_key = #{userKey,jdbcType=CHAR}


