select 
 t_u.* 
 from zj_xm_salary_user_extension  t_u 
                                       INNER JOIN 
      zj_xm_salary_user_department t_ud 
                                       ON t_u.extension_id = t_ud.extension_id

 where t_u.del_flag = '0' and t_ud.del_flag = '0' 

  and t_ud.department_id = #{departmentId,jdbcType=CHAR}


