mapper.select:
    select 
    ,
    (
		select case when sum(round(B.balAmt,2)) is null then 0 else sum(round(B.balAmt,2)) end from zx_ct_Balance B WHERE B.period < A.period and B.orgID = A.orgID
	) AS totalLastBalAmt,
	(
		select case when sum(round(B.balAmt,2)) is null then 0 else sum(round(B.balAmt,2)) end from zx_ct_Balance B WHERE B.period <= A.period and B.orgID = A.orgID
	) AS totalbalAmt
    from zx_ct_Balance AS A
    where del_flag = '0' and id = #{id,jdbcType=CHAR}

    select 
	    ,
	    (
			select case when sum(round(B.balAmt,2)) is null then 0 else sum(round(B.balAmt,2)) end from zx_ct_Balance B WHERE B.period < A.period and B.orgID = A.orgID
		) AS totalLastBalAmt,
		(
			select case when sum(round(B.balAmt,2)) is null then 0 else sum(round(B.balAmt,2)) end from zx_ct_Balance B WHERE B.period <= A.period and B.orgID = A.orgID
		) AS totalbalAmt
	from zx_ct_Balance AS A
    where del_flag = '0'

     and id = #{id,jdbcType=CHAR}


     and orgID = #{orgID,jdbcType=CHAR}


     and workBookID = #{workBookID,jdbcType=VARCHAR}


     and contractID = #{contractID,jdbcType=VARCHAR}


