mapper.update:
        update zx_ct_Balance
		set balAmt = (
			SELECT IF(ISNULL(SUM(tab1.balAmt)),0,SUM(tab1.balAmt)) FROM
			(
				SELECT balAmt AS balAmt FROM zx_ct_balanceitem WHERE del_flag = '0' AND balID = #{id,jdbcType=CHAR}
				UNION ALL
				SELECT totalAmt AS balAmt FROM zx_ct_daywork WHERE del_flag = '0' AND balID = #{id,jdbcType=CHAR}
			) AS tab1
		)
		WHERE del_flag = '0'
		AND id = #{id,jdbcType=CHAR}
