mapper.select:
select 

from zx_ct_Works
where del_flag = '0' and id = #{id,jdbcType=CHAR}

select 

from zx_ct_Works
where del_flag = '0'

 and id = #{id,jdbcType=CHAR}


 and parentID = #{parentID,jdbcType=VARCHAR}


 and treeNode = #{treeNode,jdbcType=VARCHAR}


 and orgID = #{orgID,jdbcType=VARCHAR}


 and workBookID = #{workBookID,jdbcType=VARCHAR}


 and workType = #{workType,jdbcType=VARCHAR}


 and workNo = #{workNo,jdbcType=VARCHAR}


 and workName = #{workName,jdbcType=VARCHAR}


 and unit = #{unit,jdbcType=VARCHAR}


 and contractPrice = #{contractPrice,jdbcType=DECIMAL}


 and contractQty = #{contractQty,jdbcType=DECIMAL}


 and contractAmt = #{contractAmt,jdbcType=DECIMAL}


 and quantity = #{quantity,jdbcType=DECIMAL}


 and price = #{price,jdbcType=DECIMAL}


 and deleted = #{deleted,jdbcType=INTEGER}


 and isLeaf = #{isLeaf,jdbcType=INTEGER}


 and exsitStatus = #{exsitStatus,jdbcType=INTEGER}


 and isAssignable = #{isAssignable,jdbcType=INTEGER}


 and updateFlag = #{updateFlag,jdbcType=VARCHAR}


 and combProp = #{combProp,jdbcType=VARCHAR}


 and pp1 = #{pp1,jdbcType=VARCHAR}


 and pp2 = #{pp2,jdbcType=VARCHAR}


 and pp3 = #{pp3,jdbcType=VARCHAR}


 and pp4 = #{pp4,jdbcType=VARCHAR}


 and pp5 = #{pp5,jdbcType=VARCHAR}


 and pp6 = #{pp6,jdbcType=VARCHAR}


 and pp7 = #{pp7,jdbcType=VARCHAR}


 and pp8 = #{pp8,jdbcType=VARCHAR}


 and pp9 = #{pp9,jdbcType=VARCHAR}


 and pp10 = #{pp10,jdbcType=VARCHAR}


 and editTime = #{editTime,jdbcType=TIMESTAMP}


 and checkQty = #{checkQty,jdbcType=DECIMAL}


 and expectChangeQty = #{expectChangeQty,jdbcType=DECIMAL}


 and expectChangePrice = #{expectChangePrice,jdbcType=DECIMAL}


 and inputWorkType = #{inputWorkType,jdbcType=VARCHAR}


 and isReCountAmt = #{isReCountAmt,jdbcType=VARCHAR}


 and qty = #{qty,jdbcType=DECIMAL}


 and isGroup = #{isGroup,jdbcType=VARCHAR}


 and contractPriceNoTax = #{contractPriceNoTax,jdbcType=DECIMAL}


 and priceNoTax = #{priceNoTax,jdbcType=DECIMAL}


 and contractAmtNoTax = #{contractAmtNoTax,jdbcType=DECIMAL}


 and amtNoTax = #{amtNoTax,jdbcType=DECIMAL}


 and taxRate = #{taxRate,jdbcType=VARCHAR}


 and remarks = #{remarks,jdbcType=VARCHAR}


 and sort = #{sort,jdbcType=INTEGER}


 and create_time = #{createTime,jdbcType=TIMESTAMP}


 and create_user = #{createUser,jdbcType=CHAR}


 and create_user_name = #{createUserName,jdbcType=VARCHAR}


 and modify_time = #{modifyTime,jdbcType=TIMESTAMP}


 and modify_user = #{modifyUser,jdbcType=CHAR}


 and modify_user_name = #{modifyUserName,jdbcType=VARCHAR}

ORDER BY treeNode

SELECT ( SELECT SUM(iw2.contractPrice * iw2.contractQty) FROM zx_ct_works AS iw2 WHERE iw2.del_flag = '0' AND iw2.orgID = #{orgID,jdbcType=VARCHAR} AND iw2.treeNode LIKE CONCAT('', iw.treeNode, '%') AND iw2.isLeaf = '1' ) AS contractAmt, ( SELECT SUM(iw2.contractPrice * iw2.checkQty) FROM zx_ct_works AS iw2 WHERE iw2.del_flag = '0' AND iw2.orgID = #{orgID,jdbcType=VARCHAR} AND iw2.treeNode LIKE CONCAT('', iw.treeNode, '%') AND iw2.isLeaf = '1' ) AS checkAmt, ( SELECT SUM(iw2.price * iw2.quantity) FROM zx_ct_works AS iw2 WHERE iw2.del_flag = '0' AND iw2.orgID = #{orgID,jdbcType=VARCHAR} AND iw2.treeNode LIKE CONCAT('', iw.treeNode, '%') AND iw2.isLeaf = '1' ) AS changeAmt, iw.id, iw.parentID, iw.treeNode, iw.orgID, iw.workBookID, iw.workType, iw.workNo, iw.workName, iw.unit, iw.contractPrice, iw.contractQty, iw.quantity, iw.price, iw.deleted, iw.isLeaf, iw.exsitStatus, iw.isAssignable, iw.updateFlag, iw.combProp, iw.pp1, iw.pp2, iw.pp3, iw.pp4, iw.pp5, iw.pp6, iw.pp7, iw.pp8, iw.pp9, iw.pp10, iw.editTime, iw.checkQty, iw.expectChangeQty, iw.expectChangePrice, iw.inputWorkType, iw.isReCountAmt, iw.qty, iw.isGroup, iw.contractPriceNoTax, iw.priceNoTax, iw.contractAmtNoTax, iw.amtNoTax, iw.taxRate, iw.remarks, iw.sort, iw.del_flag, iw.create_time, iw.create_user, iw.create_user_name, iw.modify_time, iw.modify_user, iw.modify_user_name FROM zx_ct_works AS iw WHERE iw.del_flag = '0' AND iw.orgID = #{orgID,jdbcType=VARCHAR}
and iw.parentID = #{parentID,jdbcType=VARCHAR}
and iw.workBookID = #{workBookID,jdbcType=VARCHAR}
ORDER BY iw.treeNode

SELECT t1.*, t2.balQty, t2.balAltQty, t2.changeAltAmt, t2.id AS balItemId, t2.balID, ( SELECT case when sum(round(BI.balQty + BI.balAltQty,2)) is null then 0 else sum(round(BI.balQty + BI.balAltQty,2)) end FROM zx_ct_Balance B INNER JOIN zx_ct_BalanceItem BI on BI.balID = B.ID WHERE B.period < (SELECT B2.period FROM zx_ct_Balance B2 where B2.ID = balID) and B.orgID = #{orgID,jdbcType=VARCHAR} AND BI.workID = t1.id ) AS lastTotalQty, ( SELECT case when sum(round(BI.balAmt,2)) is null then 0 else sum(round(BI.balAmt,2)) end FROM zx_ct_Balance B INNER JOIN zx_ct_BalanceItem BI on BI.balID = B.ID WHERE B.period < (SELECT B2.period FROM zx_ct_Balance B2 where B2.ID = balID) and B.orgID = #{orgID,jdbcType=VARCHAR} AND BI.workID = t1.id ) AS lastTotalAmt, ( SELECT case when sum(round(BI.balQty + BI.balAltQty,2)) is null then 0 else sum(round(BI.balQty + BI.balAltQty,2)) end FROM zx_ct_Balance B INNER JOIN zx_ct_BalanceItem BI on BI.balID = B.ID WHERE B.period <= (SELECT B2.period FROM zx_ct_Balance B2 where B2.ID = balID) and B.orgID = #{orgID,jdbcType=VARCHAR} AND BI.workID = t1.id ) AS thisTotalQty, ( SELECT case when sum(round(BI.balAmt,2)) is null then 0 else sum(round(BI.balAmt,2)) end FROM zx_ct_Balance B INNER JOIN zx_ct_BalanceItem BI on BI.balID = B.ID WHERE B.period <= (SELECT B2.period FROM zx_ct_Balance B2 where B2.ID = balID) and B.orgID = #{orgID,jdbcType=VARCHAR} AND BI.workID = t1.id ) AS thisTotalAmt FROM zx_ct_works AS t1 LEFT JOIN zx_ct_balanceitem AS t2 ON t1.id = t2.workID AND t2.del_flag = '0' AND t2.balID = #{balID,jdbcType=VARCHAR} WHERE t1.del_flag = '0' AND t1.orgID = #{orgID,jdbcType=VARCHAR}
and t1.parentID = #{parentID,jdbcType=VARCHAR}
and t1.workBookID = #{workBookID,jdbcType=VARCHAR}
ORDER BY t1.treeNode

SELECT t1.*, t2.balQty, t2.balAltQty, t2.changeAltAmt, t2.id AS balItemId, t2.balID, ( SELECT case when sum(round(BI.balQty + BI.balAltQty,2)) is null then 0 else sum(round(BI.balQty + BI.balAltQty,2)) end FROM zx_ct_Balance B INNER JOIN zx_ct_BalanceItem BI on BI.balID = B.ID WHERE B.period < (SELECT B2.period FROM zx_ct_Balance B2 where B2.ID = balID) and B.orgID = #{orgID,jdbcType=VARCHAR} AND BI.workID = t1.id ) AS lastTotalQty, ( SELECT case when sum(round(BI.balAmt,2)) is null then 0 else sum(round(BI.balAmt,2)) end FROM zx_ct_Balance B INNER JOIN zx_ct_BalanceItem BI on BI.balID = B.ID WHERE B.period < (SELECT B2.period FROM zx_ct_Balance B2 where B2.ID = balID) and B.orgID = #{orgID,jdbcType=VARCHAR} AND BI.workID = t1.id ) AS lastTotalAmt, ( SELECT case when sum(round(BI.balQty + BI.balAltQty,2)) is null then 0 else sum(round(BI.balQty + BI.balAltQty,2)) end FROM zx_ct_Balance B INNER JOIN zx_ct_BalanceItem BI on BI.balID = B.ID WHERE B.period <= (SELECT B2.period FROM zx_ct_Balance B2 where B2.ID = balID) and B.orgID = #{orgID,jdbcType=VARCHAR} AND BI.workID = t1.id ) AS thisTotalQty, ( SELECT case when sum(round(BI.balAmt,2)) is null then 0 else sum(round(BI.balAmt,2)) end FROM zx_ct_Balance B INNER JOIN zx_ct_BalanceItem BI on BI.balID = B.ID WHERE B.period <= (SELECT B2.period FROM zx_ct_Balance B2 where B2.ID = balID) and B.orgID = #{orgID,jdbcType=VARCHAR} AND BI.workID = t1.id ) AS thisTotalAmt FROM zx_ct_works AS t1 LEFT JOIN zx_ct_balanceitem AS t2 ON t1.id = t2.workID AND t2.del_flag = '0' AND t2.balID = #{balID,jdbcType=VARCHAR} WHERE t1.del_flag = '0' AND t1.orgID = #{orgID,jdbcType=VARCHAR} AND t1.isLeaf = '1' AND t1.parentID != '-1'
and t1.parentID = #{parentID,jdbcType=VARCHAR}
and t1.workBookID = #{workBookID,jdbcType=VARCHAR}
ORDER BY t1.treeNode

mapper.update:
update zx_ct_Works set 
	isLeaf = #{isLeaf,jdbcType=INTEGER}
where id = #{id,jdbcType=CHAR}

mapper.delete:
		delete 
		FROM zx_ct_Works
        where 1 = 1 

	    	and parentID = #{parentID,jdbcType=VARCHAR}


	    	and orgID = #{orgID,jdbcType=VARCHAR}

mapper.update:
