select 
,
id AS zxCtEqContrItemId,
(SELECT catName FROM zx_eq_ResCategory AS t WHERE del_flag = '0' AND t.id = t1.parentID) AS parentName
from zx_eq_ResCategory AS t1
where del_flag = '0' 
      AND parentID !=(SELECT bsid FROM zx_eq_rescategory WHERE del_flag = '0' AND parentID = '0000' )
      AND parentID != '0000'

 and parentID = #{parentID,jdbcType=VARCHAR}


 and bsid = #{bsid,jdbcType=VARCHAR}


 and catCode = #{catCode,jdbcType=VARCHAR}


 and catName = #{catName,jdbcType=VARCHAR}


