mapper.delete:
delete from zx_gcsg_cc_co_alter_work
where del_flag = '0'

 and cc_co_alter_work_id = #{ccCoAlterWorkId,jdbcType=CHAR}


 and cc_co_alter_id = #{ccCoAlterId,jdbcType=VARCHAR}


 and ct_contr_apply_id = #{ctContrApplyId,jdbcType=VARCHAR}


 and alterType = #{alterType,jdbcType=VARCHAR}

mapper.select:
select caw.cc_co_alter_work_id, caw.cc_co_alter_id, caw.alterType, caw.muID, caw.cc_works_id, caw.originQty, caw.originPrice, caw.applyQty, caw.applyPrice, caw.applyAddQty, caw.replyQty, caw.replyPrice, caw.replyAddQty, caw.combProp, caw.pp1, caw.pp2, caw.pp3, caw.pp4, caw.pp5, caw.pp6, caw.pp7, caw.pp8, caw.pp9, caw.pp10, caw.cc_works_parent_no, caw.cc_works_name, caw.cc_works_unit, caw.cc_works_no, caw.cc_works_parent_id, caw.mount, caw.editTime, caw.changeQty, caw.changePrice, caw.contractPrice, caw.afterChangeQty, caw.afterChangePrice, caw.isLeaf, caw.requestEdit, caw.editUserID, caw.editUserName, caw.editDate, caw.originPriceNoTax, caw.contractCostNoTax, caw.taxRate, caw.afterAmt, caw.afterAmtNoTax, caw.afterAmtTax, caw.ruleID, caw.ruleName, caw.opinion_field1, caw.opinion_field2, caw.opinion_field3, caw.opinion_field4, caw.opinion_field5, caw.opinion_field6, caw.opinion_field7, caw.opinion_field8, caw.opinion_field9, caw.opinion_field10, caw.apih5_flow_id, caw.apih5_flow_status, caw.apih5_flow_node_status, caw.work_id, caw.ct_contr_apply_id, caw.treeNode, caw.remarks, caw.sort, caw.del_flag, caw.create_time, caw.create_user, caw.create_user_name, caw.modify_time, caw.modify_user, caw.modify_user_name, (SELECT COUNT(*) FROM zx_gcsg_ct_contr_process_guajie cpg WHERE cpg.del_flag = '0' and cpg.apply_alter_works_id = caw.cc_co_alter_work_id) as gjNum from zx_gcsg_cc_co_alter_work caw where caw.del_flag = '0'
and caw.cc_co_alter_id = #{ccCoAlterId,jdbcType=VARCHAR}
and caw.ct_contr_apply_id = #{ctContrApplyId,jdbcType=VARCHAR}

select * from (select caw.cc_co_alter_work_id as ccCoAlterWorkId, caw.cc_works_unit as unit, caw.cc_works_no as workNo, caw.cc_works_name as workName, ccp.processName, ccp.content, ccp.processNo, caw.ruleName, ccp.id as processID from zx_gcsg_cc_co_alter_work caw INNER JOIN zx_gcsg_ct_contr_process_guajie cpg ON caw.cc_co_alter_work_id = cpg.apply_alter_works_id INNER JOIN zx_ct_ContrProcess ccp ON cpg.processID = ccp.id where caw.del_flag = '0' AND cpg.del_flag = '0' AND ccp.del_flag = '0' AND cpg.baseType = '1' AND cpg.isLeaf = 1
AND caw.ct_contr_apply_id = #{ctContrApplyId,jdbcType=VARCHAR}
UNION ALL select caw.cc_co_alter_work_id as ccCoAlterWorkId, caw.cc_works_unit as unit, caw.cc_works_no as workNo, caw.cc_works_name as workName, csp.processName,csp.content, csp.processNo, caw.ruleName, csp.id as processID from zx_gcsg_cc_co_alter_work caw INNER JOIN zx_gcsg_ct_contr_process_guajie cpg ON caw.cc_co_alter_work_id = cpg.apply_alter_works_id INNER JOIN zx_ct_SZProcess csp ON cpg.processID = csp.id where caw.del_flag = '0' AND cpg.del_flag = '0' AND csp.del_flag = '0' AND cpg.baseType != '1' AND cpg.isLeaf = 1
AND caw.ct_contr_apply_id = #{ctContrApplyId,jdbcType=VARCHAR}
) as new_t ORDER BY ccCoAlterWorkId

select 
      ccaw.*,
      cw.quantity as quantity
from zx_gcsg_cc_co_alter_work ccaw left join zx_gcsg_cc_works cw
                                   on ccaw.cc_works_id = cw.cc_works_id
                                   and cw.del_flag = '0'
where ccaw.del_flag = '0'

 and ccaw.cc_co_alter_work_id = #{ccCoAlterWorkId,jdbcType=CHAR}


 and ccaw.cc_co_alter_id = #{ccCoAlterId,jdbcType=VARCHAR}


 and ccaw.alterType = #{alterType,jdbcType=VARCHAR}


 and ccaw.muID = #{muID,jdbcType=VARCHAR}


 and ccaw.cc_works_id = #{ccWorksId,jdbcType=VARCHAR}


 and ccaw.originQty = #{originQty,jdbcType=DECIMAL}


 and ccaw.cc_works_parent_no = #{ccWorksParentNo,jdbcType=VARCHAR}


 and ccaw.cc_works_name = #{ccWorksName,jdbcType=VARCHAR}


 and ccaw.cc_works_unit = #{ccWorksUnit,jdbcType=VARCHAR}


 and ccaw.cc_works_no = #{ccWorksNo,jdbcType=VARCHAR}


 and ccaw.cc_works_parent_id = #{ccWorksParentId,jdbcType=VARCHAR}


 and ccaw.isLeaf = #{isLeaf,jdbcType=INTEGER}


 and ccaw.ruleID = #{ruleID,jdbcType=VARCHAR}


 and ccaw.apih5_flow_status = #{apih5FlowStatus,jdbcType=CHAR}


 and ccaw.work_id = #{workId,jdbcType=CHAR}


 and ccaw.ct_contr_apply_id = #{ctContrApplyId,jdbcType=VARCHAR}


 and ccaw.treeNode = #{treeNode,jdbcType=VARCHAR}

ORDER BY ccaw.modify_time DESC
