mapper.select:
select caw.*, ca.isDeduct as isDeduct, (SELECT COUNT(*) FROM zx_gcsg_ct_contr_process_guajie cpg WHERE cpg.del_flag = '0' and cpg.isLeaf = 1 and cpg.apply_alter_works_id = caw.ct_contr_apply_works_id) as processNum from zx_gcsg_ct_contr_apply_works caw INNER JOIN zx_gcsg_ct_contr_apply ca ON caw.ct_contr_apply_id = ca.ct_contr_apply_id where caw.del_flag = '0' AND ca.del_flag = '0'
and caw.parentID = #{parentID,jdbcType=VARCHAR} or caw.ct_contr_apply_works_id = #{parentID,jdbcType=VARCHAR}
and caw.ct_contr_apply_id = #{ctContrApplyId,jdbcType=VARCHAR}
and caw.treeNode like CONCAT('%', #{treeNode,jdbcType=VARCHAR}, '%')
and caw.isLeaf = #{isLeaf,jdbcType=INTEGER}
ORDER BY caw.treeNode ASC

select 
      caw.*,
      ca.isDeduct as isDeduct
 from zx_gcsg_ct_contr_apply_works caw  INNER JOIN zx_gcsg_ct_contr_apply ca
                                       ON caw.ct_contr_apply_id = ca.ct_contr_apply_id
where caw.del_flag = '0' AND ca.del_flag = '0'

 and caw.ct_contr_apply_works_id = #{ctContrApplyWorksId,jdbcType=CHAR}


 and caw.parentID = #{parentID,jdbcType=VARCHAR}


 and caw.treeNode = #{treeNode,jdbcType=VARCHAR}


 and caw.orgID = #{orgID,jdbcType=VARCHAR}


 and caw.ct_contr_apply_id = #{ctContrApplyId,jdbcType=VARCHAR}

ORDER BY caw.treeNode ASC

select * from (select caw.ct_contr_apply_works_id as ctContrApplyWorksId, caw.unit, caw.workNo, caw.workName, ccp.processName, ccp.content, ccp.processNo, caw.ruleName, ccp.id as processID from zx_gcsg_ct_contr_apply_works caw INNER JOIN zx_gcsg_ct_contr_process_guajie cpg ON caw.ct_contr_apply_works_id = cpg.apply_alter_works_id INNER JOIN zx_ct_ContrProcess ccp ON cpg.processID = ccp.id where caw.del_flag = '0' AND cpg.del_flag = '0' AND ccp.del_flag = '0' AND cpg.baseType = 'contr' AND cpg.isLeaf = 1
AND caw.ct_contr_apply_id = #{ctContrApplyId,jdbcType=VARCHAR}
UNION ALL select caw.ct_contr_apply_works_id as ctContrApplyWorksId, caw.unit, caw.workNo, caw.workName,csp.processName,csp.content, csp.processNo, caw.ruleName, csp.id as processID from zx_gcsg_ct_contr_apply_works caw INNER JOIN zx_gcsg_ct_contr_process_guajie cpg ON caw.ct_contr_apply_works_id = cpg.apply_alter_works_id INNER JOIN zx_ct_SZProcess csp ON cpg.processID = csp.id where caw.del_flag = '0' AND cpg.del_flag = '0' AND csp.del_flag = '0' AND cpg.baseType != 'contr' AND cpg.isLeaf = 1
AND caw.ct_contr_apply_id = #{ctContrApplyId,jdbcType=VARCHAR}
) as new_t ORDER BY ctContrApplyWorksId

select 

from zx_gcsg_ct_contr_apply_works
where del_flag = '0' 

 and ct_contr_apply_works_id = #{ctContrApplyWorksId,jdbcType=CHAR}


 and ct_contr_apply_id = #{ctContrApplyId,jdbcType=VARCHAR}
