    and isCompany = #{isCompany,jdbcType=VARCHAR}


    and checkType = #{checkType,jdbcType=VARCHAR}


    and reportID = #{reportID,jdbcType=VARCHAR}


    and status = #{status,jdbcType=VARCHAR}


    and isSend = #{isSend,jdbcType=VARCHAR}


    and isReported = #{isReported,jdbcType=VARCHAR}


    and sendID = #{sendID,jdbcType=VARCHAR}


    and checkAgainStatus = #{checkAgainStatus,jdbcType=VARCHAR}


    and companyId = #{companyId,jdbcType=VARCHAR}


    and companyName = #{companyName,jdbcType=VARCHAR}


    and remarks = #{remarks,jdbcType=VARCHAR}


    and sort = #{sort,jdbcType=INTEGER}


    and create_time = #{createTime,jdbcType=TIMESTAMP}


    and create_user = #{createUser,jdbcType=CHAR}


    and create_user_name = #{createUserName,jdbcType=VARCHAR}


    and modify_time = #{modifyTime,jdbcType=TIMESTAMP}


    and modify_user = #{modifyUser,jdbcType=CHAR}


    and modify_user_name = #{modifyUserName,jdbcType=VARCHAR}

ORDER BY modify_time DESC

