SELECT count( a.orgID ) AS orgNum, count( c.orgID ) AS checkNum, count( a.orgID ) - count( c.orgID ) AS noCheckNum FROM zx_ct_contract a LEFT JOIN sys_project b ON b.department_id = a.orgID LEFT JOIN zx_sf_check c ON c.orgID = a.orgID WHERE CASE WHEN ISNULL( b.doc_date ) THEN DATE_FORMAT( b.delivery_date, '%Y%m%d' ) <= #{today,jdbcType=VARCHAR} ELSE ( DATE_FORMAT( b.delivery_date, '%Y%m%d' ) <= #{today} AND DATE_FORMAT( b.doc_date, '%Y%m%d' ) >#{today,jdbcType=VARCHAR} ) END AND c.del_flag ='0'
AND a.company_id=#{record.companyId,jdbcType=VARCHAR}
AND c.checkDate>#{record.startDate,jdbcType=TIMESTAMP}
AND c.checkDate <#{record.endDate,jdbcType=TIMESTAMP}

SELECT count( a.orgID ) AS orgNum, count( c.orgID ) AS checkNum, count( a.orgID ) - count( c.orgID ) AS noCheckNum FROM zx_ct_contract a LEFT JOIN sys_project b ON b.department_id = a.orgID LEFT JOIN zx_sf_check c ON c.orgID = a.orgID WHERE CASE WHEN ISNULL( b.delivery_date ) THEN ( CASE WHEN ISNULL( b.doc_date ) THEN DATE_FORMAT( b.main_end_date, '%Y%m%d' ) <= #{today,jdbcType=VARCHAR} ELSE ( DATE_FORMAT( b.main_end_date, '%Y%m%d' ) <= #{today,jdbcType=VARCHAR} AND DATE_FORMAT( b.doc_date, '%Y%m%d' ) > #{today,jdbcType=VARCHAR} ) END ) ELSE ( DATE_FORMAT( b.main_end_date, '%Y%m%d' ) <= #{today,jdbcType=VARCHAR} AND DATE_FORMAT( b.delivery_date, '%Y%m%d' ) > #{today,jdbcType=VARCHAR} ) END AND c.del_flag ='0'
AND a.company_id=#{record.companyId,jdbcType=VARCHAR}
AND c.checkDate>#{record.startDate,jdbcType=TIMESTAMP}
AND c.checkDate <#{record.endDate,jdbcType=TIMESTAMP}

SELECT count( a.orgID ) AS orgNum, count( c.projID ) AS checkNum, count( a.orgID ) - count( c.orgID ) AS noCheckNum FROM zx_ct_contract a LEFT JOIN sys_project b ON b.department_id = a.orgID LEFT JOIN zx_sf_check c ON c.orgID = a.orgID WHERE CASE
WHEN ISNULL( b.main_end_date ) THEN ( CASE
WHEN ISNULL( b.delivery_date ) THEN ( CASE WHEN ISNULL( b.doc_date ) THEN DATE_FORMAT( b.actual_start_date, '%Y%m%d' ) <= #{today,jdbcType=VARCHAR} ELSE ( DATE_FORMAT( b.actual_start_date, '%Y%m%d' ) <= #{today,jdbcType=VARCHAR} AND DATE_FORMAT( b.doc_date, '%Y%m%d' ) > #{today,jdbcType=VARCHAR} ) END ) ELSE ( DATE_FORMAT( b.actual_start_date, '%Y%m%d' ) <= #{today,jdbcType=VARCHAR} AND DATE_FORMAT( b.delivery_date, '%Y%m%d' ) > #{today,jdbcType=VARCHAR} ) END ) ELSE ( DATE_FORMAT( b.actual_start_date, '%Y%m%d' ) <= #{today,jdbcType=VARCHAR} AND DATE_FORMAT( b.main_end_date, '%Y%m%d' ) > #{today,jdbcType=VARCHAR} ) END AND c.del_flag ='0'
AND a.company_id=#{record.companyId,jdbcType=VARCHAR}
AND c.checkDate>#{record.startDate,jdbcType=TIMESTAMP}
AND c.checkDate <#{record.endDate,jdbcType=TIMESTAMP}

