WHEN ISNULL( b.doc_date ) THEN DATE_FORMAT( b.actual_start_date, '%Y%m%d' ) <= #{today,jdbcType=VARCHAR} ELSE ( DATE_FORMAT( b.actual_start_date, '%Y%m%d' ) <=#{today,jdbcType=VARCHAR} AND DATE_FORMAT( b.doc_date, '%Y%m%d' ) > #{today,jdbcType=VARCHAR} ) END ) ELSE ( DATE_FORMAT( b.actual_start_date, '%Y%m%d' ) <= #{today,jdbcType=VARCHAR} AND DATE_FORMAT( b.delivery_date, '%Y%m%d' ) > #{today,jdbcType=VARCHAR} ) END ) ELSE ( DATE_FORMAT( b.actual_start_date, '%Y%m%d' ) <= #{today,jdbcType=VARCHAR} AND DATE_FORMAT( b.main_end_date, '%Y%m%d' ) > #{today,jdbcType=VARCHAR} ) END AND c.del_flag ='0'
AND a.company_id=#{record.companyId,jdbcType=VARCHAR}
