SELECT a.company_id, a.company_name, count( a.orgID ) AS orgNum, count( b.orgID ) AS checkNum, count( a.orgID ) - count( b.orgID ) AS noCheckNum FROM zx_ct_contract a LEFT JOIN zx_sf_check b ON a.orgID = b.orgID WHERE b.del_flag ='0'
AND b.checkDate>#{record.startDate,jdbcType=TIMESTAMP}
AND b.checkDate <#{record.endDate,jdbcType=TIMESTAMP}
GROUP BY a.company_id, a.company_name

