SELECT a.zxSfCheckId, a.orgID, a.orgName, a.checkDate, a.checkContext, a.checkGroup, a.checkEmployee, #{startDate} as startDate, #{endDate} as endDate, count( b.riskInfo ) AS riskNum, count( CASE WHEN b.checkResult = '合格' THEN 1 ELSE NULL END ) AS zGaiNum FROM zx_sf_check a LEFT JOIN zx_sf_checkitem b ON a.zxSfCheckId = b.checkID where a.del_flag = '0'
and a.orgID = #{orgID,jdbcType=VARCHAR}
and a.checkDate >= #{startDate,jdbcType=TIMESTAMP}
and a.checkDate <= #{endDate,jdbcType=TIMESTAMP}
GROUP BY a.zxSfCheckId
